import{_ as i,d as l}from"./app.fcb85f29.js";var r="/myBlog-vp/assets/jcjc1.d68eb965.png",n="/myBlog-vp/assets/jcjc2.0c87a48c.png",s="/myBlog-vp/assets/qx01.555f607d.png";const t={},o=l('<h1 id="\u7EE7\u627F-\u5C42\u53E0-\u5143\u7D20\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u7EE7\u627F-\u5C42\u53E0-\u5143\u7D20\u7C7B\u578B" aria-hidden="true">#</a> \u7EE7\u627F - \u5C42\u53E0 - \u5143\u7D20\u7C7B\u578B</h1><h2 id="css\u7684\u5C5E\u6027\u7EE7\u627F" tabindex="-1"><a class="header-anchor" href="#css\u7684\u5C5E\u6027\u7EE7\u627F" aria-hidden="true">#</a> CSS\u7684\u5C5E\u6027\u7EE7\u627F</h2><p>CSS\u7684\u67D0\u4E9B\u5C5E\u6027\u5177\u6709\u7EE7\u627F\u6027(Inherited):</p><ul><li>\u5982\u679C\u4E00\u4E2A\u5C5E\u6027\u5177\u5907\u7EE7\u627F\u6027, \u90A3\u4E48\u5728\u8BE5\u5143\u7D20\u4E0A\u8BBE\u7F6E\u540E, \u5B83\u7684\u540E\u4EE3\u5143\u7D20\u90FD\u53EF\u4EE5\u7EE7\u627F\u8FD9\u4E2A\u5C5E\u6027;</li><li>\u5F53\u7136, \u5982\u679C\u540E\u4EE3\u5143\u7D20\u81EA\u5DF1\u6709\u8BBE\u7F6E\u8BE5\u5C5E\u6027, \u90A3\u4E48\u4F18\u5148\u4F7F\u7528\u540E\u4EE3\u5143\u7D20\u81EA\u5DF1\u7684\u5C5E\u6027(\u4E0D\u7BA1\u7EE7\u627F\u8FC7\u6765\u7684\u5C5E\u6027\u6743\u91CD\u591A\u9AD8);</li></ul><p>\u5982\u4F55\u77E5\u9053\u4E00\u4E2A\u5C5E\u6027\u662F\u5426\u5177\u6709\u7EE7\u627F\u6027\u5462?</p><ul><li>\u5E38\u89C1\u7684 font-size/font-family/font-weight/line-height/color/text-align\u90FD\u5177\u6709\u7EE7\u627F\u6027;</li><li>\u8FD9\u4E9B<strong>\u4E0D\u7528\u523B\u610F\u53BB\u8BB0</strong>, \u7528\u7684\u591A\u81EA\u7136\u5C31\u8BB0\u4F4F\u4E86</li></ul><p>\u8981\u770B\u6587\u6863 <img src="'+r+'" alt="\u56FE\u7247"></p><p><strong>\u6CE8\u610F(\u4E86\u89E3): \u7EE7\u627F\u8FC7\u6765\u7684\u662F\u8BA1\u7B97\u503C, \u800C\u4E0D\u662F\u8BBE\u7F6E\u503C</strong></p><p>\u5E38\u89C1\u7684\u7EE7\u627F\u5C5E\u6027(\u4E0D\u7528\u80CC), \u60F3\u4E86\u89E3\u5C31\u67E5\u770B\u6587\u6863\u5C31\u884C <img src="'+n+'" alt="\u56FE\u7247"></p><h2 id="css\u5C5E\u6027\u7684\u5C42\u53E0" tabindex="-1"><a class="header-anchor" href="#css\u5C5E\u6027\u7684\u5C42\u53E0" aria-hidden="true">#</a> CSS\u5C5E\u6027\u7684\u5C42\u53E0</h2><p><em>CSS\u7684\u7FFB\u8BD1\u662F\u5C42\u53E0\u6837\u5F0F\u8868, \u4EC0\u4E48\u662F\u5C42\u53E0\u5462?</em></p><ul><li>\u5BF9\u4E8E\u4E00\u4E2A\u5143\u7D20\u6765\u8BF4, <strong>\u76F8\u540C\u4E00\u4E2A\u5C5E\u6027</strong>\u6211\u4EEC\u53EF\u4EE5<strong>\u901A\u8FC7\u4E0D\u540C\u7684\u9009\u62E9\u5668\u7ED9\u5B83\u8FDB\u884C\u591A\u6B21\u8BBE\u7F6E</strong></li><li>\u90A3\u4E48\u5C5E\u6027\u4F1A<strong>\u88AB\u4E00\u5C42\u5C42\u8986\u76D6\u4E0A\u53BB</strong></li><li>\u4F46\u662F\u6700\u7EC8<strong>\u53EA\u6709\u4E00\u4E2A\u4F1A\u751F\u6548</strong></li></ul><p>\u90A3\u4E48\u591A\u4E2A\u6837\u5F0F\u5C5E\u6027\u8986\u76D6\u4E0A\u53BB, \u54EA\u4E00\u4E2A\u4F1A\u751F\u6548\u5462?</p><ul><li><strong>\u5224\u65AD\u4E00</strong>: <strong>\u9009\u62E9\u5668\u7684\u6743\u91CD, \u6743\u91CD\u5927\u7684\u751F\u6548, \u6839\u636E\u6743\u91CD\u53EF\u4EE5\u5224\u65AD\u51FA\u4F18\u5148\u7EA7</strong></li><li><strong>\u5224\u65AD\u4E8C</strong>: <strong>\u5148\u540E\u987A\u5E8F, \u6743\u91CD\u76F8\u540C\u65F6, \u540E\u9762\u8BBE\u7F6E\u7684\u751F\u6548</strong></li></ul><p>\u90A3\u4E48\u5982\u4F55\u77E5\u9053\u5143\u7D20\u7684\u6743\u91CD\u5462?</p><h3 id="\u9009\u62E9\u5668\u7684\u6743\u91CD" tabindex="-1"><a class="header-anchor" href="#\u9009\u62E9\u5668\u7684\u6743\u91CD" aria-hidden="true">#</a> \u9009\u62E9\u5668\u7684\u6743\u91CD</h3><ul><li><strong>!important</strong>\uFF1A10000</li><li><strong>\u5185\u8054\u6837\u5F0F</strong>\uFF1A1000</li><li><strong>id\u9009\u62E9\u5668</strong>\uFF1A100</li><li><strong>\u7C7B\u9009\u62E9\u5668\u3001\u5C5E\u6027\u9009\u62E9\u5668\u3001\u4F2A\u7C7B</strong>\uFF1A10</li><li><strong>\u5143\u7D20\u9009\u62E9\u5668\u3001\u4F2A\u5143\u7D20</strong>\uFF1A1</li><li><strong>\u901A\u914D\u7B26</strong>\uFF1A0 \u5C0F\u7EC3\u4E60 : <img src="'+s+'" alt="\u56FE\u7247"></li></ul><h2 id="\u5143\u7D20\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u5143\u7D20\u7C7B\u578B" aria-hidden="true">#</a> \u5143\u7D20\u7C7B\u578B</h2><h3 id="display-\u503C" tabindex="-1"><a class="header-anchor" href="#display-\u503C" aria-hidden="true">#</a> display \u503C</h3><p>CSS\u4E2D\u6709\u4E2Adisplay\u5C5E\u6027\uFF0C\u80FD\u4FEE\u6539\u5143\u7D20\u7684\u663E\u793A\u7C7B\u578B\uFF0C\u67094\u4E2A\u5E38\u7528\u503C</p><ul><li><strong>block</strong>\uFF1A\u8BA9\u5143\u7D20\u663E\u793A\u4E3A\u5757\u7EA7\u5143\u7D20</li><li><strong>inline</strong>\uFF1A\u8BA9\u5143\u7D20\u663E\u793A\u4E3A\u884C\u5185\u7EA7\u5143\u7D20</li><li><strong>inline-block</strong>\uFF1A\u8BA9\u5143\u7D20\u540C\u65F6\u5177\u5907\u884C\u5185\u7EA7\u3001\u5757\u7EA7\u5143\u7D20\u7684\u7279\u5F81</li><li><strong>none</strong>\uFF1A\u9690\u85CF\u5143\u7D20</li></ul><h4 id="\u7279\u6027" tabindex="-1"><a class="header-anchor" href="#\u7279\u6027" aria-hidden="true">#</a> \u7279\u6027</h4><p>block\u5143\u7D20:</p><ol><li>\u72EC\u5360\u7236\u5143\u7D20\u7684\u4E00\u884C</li><li>\u53EF\u4EE5\u968F\u610F\u8BBE\u7F6E\u5BBD\u9AD8</li><li>\u9AD8\u5EA6\u9ED8\u8BA4\u7531\u5185\u5BB9\u51B3\u5B9A</li></ol><p>inline-block\u5143\u7D20:</p><ol><li>\u8DDF\u5176\u4ED6\u884C\u5185\u7EA7\u5143\u7D20\u5728\u540C\u4E00\u884C\u663E\u793A</li><li>\u53EF\u4EE5\u968F\u610F\u8BBE\u7F6E\u5BBD\u9AD8</li></ol><p>inline:</p><ol><li>\u8DDF\u5176\u4ED6\u884C\u5185\u7EA7\u5143\u7D20\u5728\u540C\u4E00\u884C\u663E\u793A;</li><li>\u4E0D\u53EF\u4EE5\u968F\u610F\u8BBE\u7F6E\u5BBD\u9AD8;</li><li>\u5BBD\u9AD8\u90FD\u7531\u5185\u5BB9\u51B3\u5B9A;</li></ol><h3 id="\u5143\u7D20\u9690\u85CF\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u5143\u7D20\u9690\u85CF\u65B9\u6CD5" aria-hidden="true">#</a> \u5143\u7D20\u9690\u85CF\u65B9\u6CD5</h3><p><strong>\u65B9\u6CD5\u4E00: display\u8BBE\u7F6E\u4E3Anone</strong></p><ul><li>\u5143\u7D20\u4E0D\u663E\u793A\u51FA\u6765, \u5E76\u4E14\u4E5F\u4E0D\u5360\u636E\u4F4D\u7F6E, \u4E0D\u5360\u636E\u4EFB\u4F55\u7A7A\u95F4(\u548C\u4E0D\u5B58\u5728\u4E00\u6837);</li></ul><p><strong>\u65B9\u6CD5\u4E8C: visibility\u8BBE\u7F6E\u4E3Ahidden</strong></p><ul><li>\u8BBE\u7F6E\u4E3A hidden, \u867D\u7136\u5143\u7D20\u4E0D\u53EF\u89C1, \u4F46\u662F\u4F1A\u5360\u636E\u5143\u7D20\u5E94\u8BE5\u5360\u636E\u7684\u7A7A\u95F4;</li><li>\u9ED8\u8BA4\u4E3A visible, \u5143\u7D20\u662F\u53EF\u89C1\u7684;</li></ul><p><strong>\u65B9\u6CD5\u4E09: rgba\u8BBE\u7F6E\u989C\u8272, \u5C06a\u7684\u503C\u8BBE\u7F6E\u4E3A0</strong></p><ul><li>rgba \u7684 A \u8BBE\u7F6E\u7684\u662Falpha\u503C, \u53EF\u4EE5\u8BBE\u7F6E\u900F\u660E\u5EA6, \u4E0D\u5F71\u54CD\u5B50\u5143\u7D20;</li></ul><p><strong>\u65B9\u6CD5\u56DB: opacity\u8BBE\u7F6E\u900F\u660E\u5EA6, \u8BBE\u7F6E\u4E3A0</strong></p><ul><li>\u8BBE\u7F6E\u6574\u4E2A\u5143\u7D20\u7684\u900F\u660E\u5EA6, \u4F1A\u5F71\u54CD\u6240\u6709\u7684\u5B50\u5143\u7D20;</li></ul><h3 id="overflow" tabindex="-1"><a class="header-anchor" href="#overflow" aria-hidden="true">#</a> overflow</h3><p>overflow \u7528\u4E8E\u63A7\u5236\u5185\u5BB9\u6EA2\u51FA\u65F6\u7684\u884C\u4E3A</p><ul><li><strong>visible</strong>\uFF1A\u6EA2\u51FA\u7684\u5185\u5BB9\u7167\u6837\u53EF\u89C1</li><li><strong>hidden</strong>\uFF1A\u6EA2\u51FA\u7684\u5185\u5BB9\u76F4\u63A5\u88C1\u526A</li><li><strong>scroll</strong>\uFF1A\u6EA2\u51FA\u7684\u5185\u5BB9\u88AB\u88C1\u526A\uFF0C\u4F46\u53EF\u4EE5\u901A\u8FC7\u6EDA\u52A8\u673A\u5236\u67E5\u770B <ul><li>\u4F1A\u4E00\u76F4\u663E\u793A\u6EDA\u52A8\u6761\u533A\u57DF\uFF0C\u6EDA\u52A8\u6761\u533A\u57DF\u5360\u7528\u7684\u7A7A\u95F4\u5C5E\u4E8Ewidth\u3001height</li></ul></li><li><strong>auto</strong>\uFF1A\u81EA\u52A8\u6839\u636E\u5185\u5BB9\u662F\u5426\u6EA2\u51FA\u6765\u51B3\u5B9A\u662F\u5426\u63D0\u4F9B\u6EDA\u52A8\u673A\u5236</li></ul>',40);function a(e,g){return o}var d=i(t,[["render",a],["__file","jicheng.html.vue"]]);export{d as default};
