import{_ as i,d as l}from"./app.fcb85f29.js";var r="/myBlog-vp/assets/mokuaihua1.15413ef2.png",o="/myBlog-vp/assets/exports1.43f91e46.png",e="/myBlog-vp/assets/exports2.e6e40123.png",t="/myBlog-vp/assets/me.2ce4e0df.png",a="/myBlog-vp/assets/moduleyinyong.0479d2d7.png",s="/myBlog-vp/assets/yygx.d5668f52.png",p="/myBlog-vp/assets/qk3.cdad40c9.png",n="/myBlog-vp/assets/tu01.49c5db72.png",d="/myBlog-vp/assets/export11.d0e4bc1b.png",m="/myBlog-vp/assets/import01.0c949dbf.png",u="/myBlog-vp/assets/exportimport.21eeb726.png",g="data:image/png;base64,",h="/myBlog-vp/assets/importhashu.d848bd12.png";const c={},x=l('<h1 id="\u6A21\u5757\u5316" tabindex="-1"><a class="header-anchor" href="#\u6A21\u5757\u5316" aria-hidden="true">#</a> \u6A21\u5757\u5316</h1><h2 id="\u6A21\u5757\u5316\u7684\u5386\u53F2" tabindex="-1"><a class="header-anchor" href="#\u6A21\u5757\u5316\u7684\u5386\u53F2" aria-hidden="true">#</a> \u6A21\u5757\u5316\u7684\u5386\u53F2</h2><p>\u5728\u7F51\u9875\u5F00\u53D1\u7684\u65E9\u671F\uFF0CBrendan Eich \u5F00\u53D1 JavaScript \u4EC5\u4EC5\u4F5C\u4E3A\u4E00\u79CD<strong>\u811A\u672C\u8BED\u8A00</strong>\uFF0C\u505A\u4E00\u4E9B\u7B80\u5355\u7684\u8868\u5355\u9A8C\u8BC1\u6216\u52A8\u753B\u5B9E\u73B0\u7B49\uFF0C\u90A3\u4E2A\u65F6 \u5019\u4EE3\u7801\u8FD8\u662F\u5F88\u5C11\u7684\uFF1A</p><ul><li>\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u53EA\u9700\u8981\u8BB2JavaScript\u4EE3\u7801\u5199\u5230 <strong><code>&lt;script&gt;</code>\u6807\u7B7E</strong>\u4E2D\u5373\u53EF\uFF1B</li><li>\u5E76\u6CA1\u6709\u5FC5\u8981\u653E\u5230\u591A\u4E2A\u6587\u4EF6\u4E2D\u6765\u7F16\u5199\uFF1B\u751A\u81F3\u6D41\u884C\uFF1A\u901A\u5E38\u6765\u8BF4 JavaScript \u7A0B\u5E8F\u7684\u957F\u5EA6\u53EA\u6709\u4E00\u884C\u3002</li></ul><p>\u4F46\u662F\u968F\u7740\u524D\u7AEF\u548C JavaScript \u7684\u5FEB\u901F\u53D1\u5C55\uFF0CJavaScript \u4EE3\u7801\u53D8\u5F97\u8D8A\u6765\u8D8A\u590D\u6742\u4E86\uFF1A</p><ul><li>ajax \u7684\u51FA\u73B0\uFF0C<strong>\u524D\u540E\u7AEF\u5F00\u53D1\u5206\u79BB</strong>\uFF0C\u610F\u5473\u7740\u540E\u7AEF\u8FD4\u56DE\u6570\u636E\u540E\uFF0C\u6211\u4EEC\u9700\u8981\u901A\u8FC7<strong>JavaScript\u8FDB\u884C\u524D\u7AEF\u9875\u9762\u7684\u6E32\u67D3</strong>\uFF1B</li><li>SPA \u7684\u51FA\u73B0\uFF0C\u524D\u7AEF\u9875\u9762\u53D8\u5F97\u66F4\u52A0\u590D\u6742\uFF1A\u5305\u62EC<strong>\u524D\u7AEF\u8DEF\u7531\u3001\u72B6\u6001\u7BA1\u7406</strong>\u7B49\u7B49\u4E00\u7CFB\u5217\u590D\u6742\u7684\u9700\u6C42\u9700\u8981\u901A\u8FC7JavaScript\u6765\u5B9E\u73B0\uFF1B</li><li>\u5305\u62ECNode\u7684\u5B9E\u73B0\uFF0CJavaScript \u7F16\u5199<strong>\u590D\u6742\u7684\u540E\u7AEF\u7A0B\u5E8F</strong>\uFF0C\u6CA1\u6709\u6A21\u5757\u5316\u662F\u81F4\u547D\u7684\u786C\u4F24\uFF1B</li></ul><p>\u6240\u4EE5\uFF0C\u6A21\u5757\u5316\u5DF2\u7ECF\u662F JavaScript \u4E00\u4E2A\u975E\u5E38\u8FEB\u5207\u7684\u9700\u6C42\uFF1A</p><ul><li>\u4F46\u662F JavaScript \u672C\u8EAB\uFF0C\u76F4\u5230<strong>ES6\uFF082015</strong>\uFF09\u624D\u63A8\u51FA\u4E86\u81EA\u5DF1\u7684\u6A21\u5757\u5316\u65B9\u6848\uFF1B</li><li>\u5728\u6B64\u4E4B\u524D\uFF0C\u4E3A\u4E86\u8BA9 JavaScript \u652F\u6301\u6A21\u5757\u5316\uFF0C\u6D8C\u73B0\u51FA\u4E86\u5F88\u591A\u4E0D\u540C\u7684\u6A21\u5757\u5316\u89C4\u8303\uFF1A<strong>AMD\u3001CMD\u3001CommonJS</strong>\u7B49\uFF1B</li></ul><h2 id="\u6CA1\u6709\u6A21\u5757\u5316\u5E26\u6765\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u6CA1\u6709\u6A21\u5757\u5316\u5E26\u6765\u7684\u95EE\u9898" aria-hidden="true">#</a> \u6CA1\u6709\u6A21\u5757\u5316\u5E26\u6765\u7684\u95EE\u9898</h2><p>\u65E9\u671F\u6CA1\u6709\u6A21\u5757\u5316\u5E26\u6765\u4E86\u5F88\u591A\u7684\u95EE\u9898\uFF1A\u6BD4\u5982\u547D\u540D\u51B2\u7A81\u7684\u95EE\u9898</p><p>\u5F53\u7136\uFF0C\u6709\u529E\u6CD5\u53EF\u4EE5\u89E3\u51B3\u4E0A\u9762\u7684\u95EE\u9898\uFF1A<strong>\u7ACB\u5373\u51FD\u6570\u8C03\u7528\u8868\u8FBE\u5F0F\uFF08IIFE\uFF09</strong></p><ul><li>IIFE (Immediately Invoked Function Expression)</li></ul><p>\u4F46\u662F\uFF0C\u6211\u4EEC\u5176\u5B9E\u5E26\u6765\u4E86\u65B0\u7684\u95EE\u9898\uFF1A</p><ul><li>\u7B2C\u4E00\uFF0C\u6211\u5FC5\u987B\u8BB0\u5F97<strong>\u6BCF\u4E00\u4E2A\u6A21\u5757\u4E2D\u8FD4\u56DE\u5BF9\u8C61\u7684\u547D\u540D</strong>\uFF0C\u624D\u80FD\u5728\u5176\u4ED6\u6A21\u5757\u4F7F\u7528\u8FC7\u7A0B\u4E2D\u6B63\u786E\u7684\u4F7F\u7528\uFF1B</li><li>\u7B2C\u4E8C\uFF0C\u4EE3\u7801\u5199\u8D77\u6765<strong>\u6DF7\u4E71\u4E0D\u582A</strong>\uFF0C\u6BCF\u4E2A\u6587\u4EF6\u4E2D\u7684\u4EE3\u7801\u90FD\u9700\u8981<strong>\u5305\u88F9\u5728\u4E00\u4E2A\u533F\u540D\u51FD\u6570\u4E2D\u6765\u7F16\u5199</strong>\uFF1B</li><li>\u7B2C\u4E09\uFF0C\u5728<strong>\u6CA1\u6709\u5408\u9002\u7684\u89C4\u8303\u60C5\u51B5</strong>\u4E0B\uFF0C\u6BCF\u4E2A\u4EBA\u3001\u6BCF\u4E2A\u516C\u53F8\u90FD\u53EF\u80FD\u4F1A\u4EFB\u610F\u547D\u540D\u3001\u751A\u81F3\u51FA\u73B0\u6A21\u5757\u540D\u79F0\u76F8\u540C\u7684\u60C5\u51B5\uFF1B</li></ul><p>\u6240\u4EE5\uFF0C\u6211\u4EEC\u4F1A\u53D1\u73B0\uFF0C\u867D\u7136\u5B9E\u73B0\u4E86\u6A21\u5757\u5316\uFF0C\u4F46\u662F\u6211\u4EEC\u7684\u5B9E\u73B0\u8FC7\u4E8E\u7B80\u5355\uFF0C\u5E76\u4E14\u662F\u6CA1\u6709\u89C4\u8303\u7684\u3002</p><ul><li>\u6211\u4EEC\u9700\u8981\u5236\u5B9A\u4E00\u5B9A\u7684\u89C4\u8303\u6765\u7EA6\u675F\u6BCF\u4E2A\u4EBA\u90FD<strong>\u6309\u7167\u8FD9\u4E2A\u89C4\u8303\u53BB\u7F16\u5199\u6A21\u5757\u5316\u7684\u4EE3\u7801</strong>\uFF1B</li><li>\u8FD9\u4E2A\u89C4\u8303\u4E2D\u5E94\u8BE5\u5305\u62EC\u6838\u5FC3\u529F\u80FD\uFF1A<strong>\u6A21\u5757\u672C\u8EAB\u53EF\u4EE5\u5BFC\u51FA\u66B4\u9732\u7684\u5C5E\u6027\uFF0C\u6A21\u5757\u53C8\u53EF\u4EE5\u5BFC\u5165\u81EA\u5DF1\u9700\u8981\u7684\u5C5E\u6027\uFF1B</strong></li><li>JavaScript \u793E\u533A\u4E3A\u4E86\u89E3\u51B3\u4E0A\u9762\u7684\u95EE\u9898\uFF0C\u6D8C\u73B0\u51FA<strong>\u4E00\u7CFB\u5217\u597D\u7528\u7684\u89C4\u8303</strong>\uFF0C\u63A5\u4E0B\u6765\u6211\u4EEC\u5C31\u5B66\u4E60\u5177\u6709\u4EE3\u8868\u6027\u7684\u4E00\u4E9B\u89C4\u8303\u3002</li></ul><h2 id="commonjs-\u89C4\u8303\u548C-node-\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#commonjs-\u89C4\u8303\u548C-node-\u5173\u7CFB" aria-hidden="true">#</a> CommonJS \u89C4\u8303\u548C Node \u5173\u7CFB</h2><p>\u6211\u4EEC\u9700\u8981\u77E5\u9053 CommonJS \u662F\u4E00\u4E2A<strong>\u89C4\u8303</strong>\uFF0C\u6700\u521D\u63D0\u51FA\u6765\u662F\u5728\u6D4F\u89C8\u5668\u4EE5\u5916\u7684\u5730\u65B9\u4F7F\u7528\uFF0C\u5E76\u4E14\u5F53\u65F6\u88AB\u547D\u540D\u4E3A <strong>ServerJS</strong>\uFF0C\u540E\u6765\u4E3A\u4E86 \u4F53\u73B0\u5B83\u7684\u5E7F\u6CDB\u6027\uFF0C\u4FEE\u6539\u4E3A <strong>CommonJS</strong>\uFF0C\u5E73\u65F6\u6211\u4EEC\u4E5F\u4F1A<strong>\u7B80\u79F0\u4E3ACJS</strong></p><ul><li>Node \u662F CommonJS \u5728\u670D\u52A1\u5668\u7AEF\u4E00\u4E2A\u5177\u6709\u4EE3\u8868\u6027\u7684\u5B9E\u73B0\uFF1B</li><li>Browserify \u662F CommonJS \u5728\u6D4F\u89C8\u5668\u4E2D\u7684\u4E00\u79CD\u5B9E\u73B0\uFF1B</li><li>webpack \u6253\u5305\u5DE5\u5177\u5177\u5907\u5BF9 CommonJS \u7684\u652F\u6301\u548C\u8F6C\u6362\uFF1B</li></ul><p>\u6240\u4EE5\uFF0CNode \u4E2D\u5BF9 <strong>CommonJS \u8FDB\u884C\u4E86\u652F\u6301\u548C\u5B9E\u73B0</strong>\uFF0C\u8BA9\u6211\u4EEC\u5728\u5F00\u53D1 node \u7684\u8FC7\u7A0B\u4E2D\u53EF\u4EE5\u65B9\u4FBF\u7684\u8FDB\u884C\u6A21\u5757\u5316\u5F00\u53D1\uFF1A</p><ul><li><strong>\u5728Node\u4E2D<em>\u6BCF\u4E00\u4E2Ajs\u6587\u4EF6\u90FD\u662F\u4E00\u4E2A\u5355\u72EC\u7684\u6A21\u5757\uFF1B</em></strong></li><li>\u8FD9\u4E2A\u6A21\u5757\u4E2D\u5305\u62EC <strong>CommonJS \u89C4\u8303\u7684\u6838\u5FC3\u53D8\u91CF</strong>\uFF1Aexports\u3001module.exports\u3001require\uFF1B</li><li>\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E9B\u53D8\u91CF\u6765\u65B9\u4FBF\u7684\u8FDB\u884C<strong>\u6A21\u5757\u5316\u5F00\u53D1</strong>\uFF1B</li></ul><p>\u524D\u9762\u6211\u4EEC\u63D0\u5230\u8FC7\u6A21\u5757\u5316\u7684\u6838\u5FC3\u662F\u5BFC\u51FA\u548C\u5BFC\u5165\uFF0CNode \u4E2D\u5BF9\u5176\u8FDB\u884C\u4E86\u5B9E\u73B0\uFF1A</p><ul><li>exports \u548C module.exports \u53EF\u4EE5\u8D1F\u8D23<strong>\u5BF9\u6A21\u5757\u4E2D\u7684\u5185\u5BB9\u8FDB\u884C\u5BFC\u51FA</strong>\uFF1B</li><li>require \u51FD\u6570\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC**\u5BFC\u5165\u5176\u4ED6\u6A21\u5757\uFF08\u81EA\u5B9A\u4E49\u6A21\u5757\u3001\u7CFB\u7EDF\u6A21\u5757\u3001\u7B2C\u4E09\u65B9\u5E93\u6A21\u5757\uFF09**\u4E2D\u7684\u5185\u5BB9\uFF1B</li></ul><h2 id="\u6A21\u5757\u5316\u6848\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u6A21\u5757\u5316\u6848\u4F8B" aria-hidden="true">#</a> \u6A21\u5757\u5316\u6848\u4F8B</h2><p><img src="'+r+'" alt="\u56FE\u7247"></p><h2 id="exports-\u5BFC\u51FA" tabindex="-1"><a class="header-anchor" href="#exports-\u5BFC\u51FA" aria-hidden="true">#</a> exports \u5BFC\u51FA</h2><p><strong>\u6CE8\u610F\uFF1Aexports \u662F\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5728\u8FD9\u4E2A\u5BF9\u8C61\u4E2D\u6DFB\u52A0\u5F88\u591A\u4E2A\u5C5E\u6027\uFF0C\u6DFB\u52A0\u7684\u5C5E\u6027\u4F1A\u5BFC\u51FA</strong></p><p><img src="'+o+'" alt="\u56FE\u7247"></p><p><strong>\u53E6\u5916\u4E00\u4E2A\u6587\u4EF6\u4E2D\u53EF\u4EE5\u5BFC\u5165\uFF1A</strong></p><p><img src="'+e+'" alt="\u56FE\u7247"></p><p>\u7406\u89E3\u4E0B\u9762\u8FD9\u53E5\u8BDD\uFF0CNode \u4E2D\u7684\u6A21\u5757\u5316\u4E00\u76EE\u4E86\u7136 :</p><ul><li><strong>\u610F\u5473\u7740 main \u4E2D\u7684 bar \u53D8\u91CF\u7B49\u4E8E exports \u5BF9\u8C61</strong></li><li>\u4E5F\u5C31\u662F require \u901A\u8FC7\u5404\u79CD\u67E5\u627E\u65B9\u5F0F\uFF0C\u6700\u7EC8\u627E\u5230\u4E86 exports \u8FD9\u4E2A\u5BF9\u8C61\uFF1B</li><li>\u5E76\u4E14\u5C06\u8FD9\u4E2A exports \u5BF9\u8C61\u8D4B\u503C\u7ED9\u4E86 bar \u53D8\u91CF\uFF1B</li><li>bar \u53D8\u91CF\u5C31\u662F exports \u5BF9\u8C61\u4E86</li></ul><h2 id="module-exports-\u5BFC\u51FA" tabindex="-1"><a class="header-anchor" href="#module-exports-\u5BFC\u51FA" aria-hidden="true">#</a> module.exports \u5BFC\u51FA</h2><p><img src="'+t+'" alt="\u56FE\u7247"></p><p><strong>module.exports \u548C exports \u6709\u4EC0\u4E48\u5173\u7CFB\u6216\u8005\u533A\u522B ??</strong></p><ul><li>CommonJS \u4E2D\u662F\u6CA1\u6709 module.exports \u7684\u6982\u5FF5\u7684\uFF1B</li><li>\u4E3A\u4E86\u5B9E\u73B0\u6A21\u5757\u7684\u5BFC\u51FA\uFF0CNode \u4E2D\u4F7F\u7528\u7684\u662F Module \u7684\u7C7B\uFF0C\u6BCF\u4E00\u4E2A\u6A21\u5757\u90FD\u662FModule\u7684\u4E00\u4E2A\u5B9E\u4F8B\uFF0C\u4E5F\u5C31\u662F module\uFF1B</li><li>\u6240\u4EE5\u5728 Node \u4E2D\u771F\u6B63\u7528\u4E8E\u5BFC\u51FA\u7684\u5176\u5B9E\u6839\u672C\u4E0D\u662F exports \uFF0C\u800C\u662F module.exports</li><li>\u56E0\u4E3A module \u624D\u662F\u5BFC\u51FA\u7684\u771F\u6B63\u5B9E\u73B0\u8005\uFF1B</li></ul><p>\u4F46\u662F\uFF0C\u4E3A\u4EC0\u4E48 exports \u4E5F\u53EF\u4EE5\u5BFC\u51FA\u5462\uFF1F</p><ul><li>\u8FD9\u662F\u56E0\u4E3A module \u5BF9\u8C61\u7684 exports \u5C5E\u6027\u662F exports \u5BF9\u8C61\u7684\u4E00\u4E2A\u5F15\u7528\uFF1B</li><li>\u4E5F\u5C31\u662F\u8BF4 module.exports = exports = main \u4E2D\u7684 bar\uFF1B</li></ul><p><img src="'+a+'" alt="\u56FE\u7247"></p><h4 id="\u6539\u4E0B\u4EE3\u7801\u601D\u8003\u4E00\u4E0B" tabindex="-1"><a class="header-anchor" href="#\u6539\u4E0B\u4EE3\u7801\u601D\u8003\u4E00\u4E0B" aria-hidden="true">#</a> \u6539\u4E0B\u4EE3\u7801\u601D\u8003\u4E00\u4E0B</h4><p><img src="'+s+'" alt="\u56FE\u7247"> \u8FD9\u91CC\u4ECE\u51E0\u4E2A\u65B9\u9762\u6765\u7814\u7A76\u4FEE\u6539\u4EE3\u7801\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F</p><ul><li>1.\u5728\u4E09\u8005\u9879\u76EE\u5F15\u7528\u7684\u60C5\u51B5\u4E0B\uFF0C\u4FEE\u6539 exports \u4E2D\u7684 name \u5C5E\u6027\u5230\u5E95\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F</li><li>2.\u5728\u4E09\u8005\u5F15\u7528\u7684\u60C5\u51B5\u4E0B\uFF0C\u4FEE\u6539\u4E86 main \u4E2D\u7684 bar \u7684 name \u5C5E\u6027\uFF0C\u5728bar \u6A21\u5757\u4E2D\u4F1A\u53D1\u751F\u4EC0\u4E48\uFF1F</li><li>3.\u5982\u679C module.exports \u4E0D\u518D\u5F15\u7528 exports \u5BF9\u8C61\u4E86\uFF0C\u90A3\u4E48\u4FEE\u6539 export \u8FD8\u6709\u610F\u4E49\u5417\uFF1F</li></ul><h2 id="require-\u7EC6\u8282" tabindex="-1"><a class="header-anchor" href="#require-\u7EC6\u8282" aria-hidden="true">#</a> require \u7EC6\u8282</h2><p><strong>require \u662F\u4E00\u4E2A\u51FD\u6570</strong>\uFF0C\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5F15\u5165\u4E00\u4E2A\u6587\u4EF6\uFF08\u6A21\u5757\uFF09\u4E2D\u5BFC\u51FA\u7684\u5BF9\u8C61</p><p><strong>\u603B\u7ED3\u6BD4\u8F83\u5E38\u89C1\u7684\u67E5\u627E\u89C4\u5219</strong>\uFF1A\u5BFC\u5165\u683C\u5F0F\u5982\u4E0B\uFF1Arequire(X)</p><p><strong>\u60C5\u51B5\u4E00</strong>\uFF1AX\u662F\u4E00\u4E2ANode\u6838\u5FC3\u6A21\u5757\uFF0C\u6BD4\u5982path\u3001http</p><ul><li>\u76F4\u63A5\u8FD4\u56DE\u6838\u5FC3\u6A21\u5757\uFF0C\u5E76\u4E14\u505C\u6B62\u67E5\u627E</li></ul><p><strong>\u60C5\u51B5\u4E8C</strong>\uFF1AX \u662F\u4EE5 ./ \u6216 ../ \u6216 /\uFF08\u6839\u76EE\u5F55\uFF09\u5F00\u5934\u7684</p><ul><li>\u7B2C\u4E00\u6B65\uFF1A\u5C06 X \u5F53\u505A\u4E00\u4E2A\u6587\u4EF6\u5728\u5BF9\u5E94\u7684\u76EE\u5F55\u4E0B\u67E5\u627E\uFF1B <ul><li>1.\u5982\u679C\u6709\u540E\u7F00\u540D\uFF0C\u6309\u7167\u540E\u7F00\u540D\u7684\u683C\u5F0F\u67E5\u627E\u5BF9\u5E94\u7684\u6587\u4EF6</li></ul></li><li>2.\u5982\u679C\u6CA1\u6709\u540E\u7F00\u540D\uFF0C\u4F1A\u6309\u7167\u5982\u4E0B\u987A\u5E8F\uFF1A <ul><li>1&gt; \u76F4\u63A5\u67E5\u627E\u6587\u4EF6 X</li><li>2&gt; \u67E5\u627E X.js \u6587\u4EF6</li><li>3&gt; \u67E5\u627E X.json \u6587\u4EF6</li><li>4&gt; \u67E5\u627E X.node \u6587\u4EF6</li></ul></li></ul><p>\u7B2C\u4E8C\u6B65\uFF1A\u6CA1\u6709\u627E\u5230\u5BF9\u5E94\u7684\u6587\u4EF6\uFF0C\u5C06 X \u4F5C\u4E3A\u4E00\u4E2A\u76EE\u5F55</p><ul><li>\u67E5\u627E\u76EE\u5F55\u4E0B\u9762\u7684 index \u6587\u4EF6</li><li>1&gt; \u67E5\u627E X/index.js \u6587\u4EF6</li><li>2&gt; \u67E5\u627E X/index.json \u6587\u4EF6</li><li>3&gt; \u67E5\u627E X/index.node \u6587\u4EF6</li></ul><p>\u5982\u679C\u6CA1\u6709\u627E\u5230\uFF0C\u90A3\u4E48\u62A5\u9519\uFF1Anot found</p><p><strong>\u60C5\u51B5\u4E09</strong>\uFF1A\u76F4\u63A5\u662F\u4E00\u4E2AX\uFF08\u6CA1\u6709\u8DEF\u5F84\uFF09\uFF0C\u5E76\u4E14X\u4E0D\u662F\u4E00\u4E2A\u6838\u5FC3\u6A21\u5757 <img src="'+p+'" alt="\u56FE\u7247"></p><p>\u5982\u679C\u4E0A\u9762\u7684\u8DEF\u5F84\u4E2D\u90FD\u6CA1\u6709\u627E\u5230\uFF0C\u90A3\u4E48\u62A5\u9519\uFF1Anot found</p><h2 id="\u6A21\u5757\u52A0\u8F7D\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u6A21\u5757\u52A0\u8F7D\u8FC7\u7A0B" aria-hidden="true">#</a> \u6A21\u5757\u52A0\u8F7D\u8FC7\u7A0B</h2><ul><li><p><strong>\u7ED3\u8BBA\u4E00 \uFF1A\u6A21\u5757\u5728\u88AB\u7B2C\u4E00\u6B21\u5F15\u5165\u65F6\uFF0C\u6A21\u5757\u4E2D\u7684 js \u4EE3\u7801\u4F1A\u88AB\u8FD0\u884C\u4E00\u6B21</strong></p></li><li><p><strong>\u7ED3\u8BBA\u4E8C\uFF1A\u6A21\u5757\u88AB\u591A\u6B21\u5F15\u5165\u65F6\uFF0C\u4F1A\u7F13\u5B58\uFF0C\u6700\u7EC8\u53EA\u52A0\u8F7D\uFF08\u8FD0\u884C\uFF09\u4E00\u6B21</strong></p><ul><li>\u4E3A\u4EC0\u4E48\u53EA\u4F1A\u52A0\u8F7D\u8FD0\u884C\u4E00\u6B21\u5462\uFF1F</li><li>\u8FD9\u662F\u56E0\u4E3A\u6BCF\u4E2A\u6A21\u5757\u5BF9\u8C61 module \u90FD\u6709\u4E00\u4E2A\u5C5E\u6027\uFF1Aloaded\u3002</li><li>\u4E3A false \u8868\u793A\u8FD8\u6CA1\u6709\u52A0\u8F7D\uFF0C\u4E3A true \u8868\u793A\u5DF2\u7ECF\u52A0\u8F7D\uFF1B</li></ul></li><li><p>\u7ED3\u8BBA\u4E09\uFF1A\u5982\u679C\u6709\u5FAA\u73AF\u5F15\u5165\uFF0C\u90A3\u4E48\u52A0\u8F7D\u987A\u5E8F\u662F\u4EC0\u4E48\uFF1F</p></li></ul><p>\u5982\u679C\u51FA\u73B0\u56FE\u6A21\u5757\u7684\u5F15\u7528\u5173\u7CFB\uFF0C\u90A3\u4E48\u52A0\u8F7D\u987A\u5E8F\u662F\u4EC0\u4E48\u5462\uFF1F</p><p><img src="'+n+'" alt="\u56FE\u7247"></p><ul><li>\u8FD9\u4E2A\u5176\u5B9E\u662F\u4E00\u79CD\u6570\u636E\u7ED3\u6784\uFF1A\u56FE\u7ED3\u6784\uFF1B</li><li>\u56FE\u7ED3\u6784\u5728\u904D\u5386\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6709\u6DF1\u5EA6\u4F18\u5148\u641C\u7D22\uFF08DFS, depth first search\uFF09\u548C\u5E7F\u5EA6\u4F18\u5148\u641C\u7D22\uFF08BFS, breadth first search\uFF09\uFF1B</li><li>Node\u91C7\u7528\u7684\u662F\u6DF1\u5EA6\u4F18\u5148\u7B97\u6CD5\uFF1Amain -&gt; aaa -&gt; ccc -&gt; ddd -&gt; eee -&gt;bbb</li></ul><h2 id="commonjs-\u89C4\u8303\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#commonjs-\u89C4\u8303\u7F3A\u70B9" aria-hidden="true">#</a> CommonJS \u89C4\u8303\u7F3A\u70B9</h2><p><em>CommonJS \u52A0\u8F7D\u6A21\u5757\u662F\u540C\u6B65\u7684\uFF1A</em></p><ul><li>\u540C\u6B65\u7684\u610F\u5473\u7740\u53EA\u6709<strong>\u7B49\u5230\u5BF9\u5E94\u7684\u6A21\u5757\u52A0\u8F7D\u5B8C\u6BD5</strong>\uFF0C<strong>\u5F53\u524D\u6A21\u5757\u4E2D\u7684\u5185\u5BB9\u624D\u80FD\u88AB\u8FD0\u884C</strong>\uFF1B</li><li>\u8FD9\u4E2A\u5728\u670D\u52A1\u5668\u4E0D\u4F1A\u6709\u4EC0\u4E48\u95EE\u9898\uFF0C\u56E0\u4E3A<strong>\u670D\u52A1\u5668\u52A0\u8F7D\u7684js\u6587\u4EF6\u90FD\u662F\u672C\u5730\u6587\u4EF6</strong>\uFF0C\u52A0\u8F7D\u901F\u5EA6\u975E\u5E38\u5FEB\uFF1B</li></ul><p><em>\u5982\u679C\u5C06\u5B83\u5E94\u7528\u4E8E\u6D4F\u89C8\u5668\u5462\uFF1F</em></p><ul><li>\u6D4F\u89C8\u5668<strong>\u52A0\u8F7Djs\u6587\u4EF6\u9700\u8981\u5148\u4ECE\u670D\u52A1\u5668\u5C06\u6587\u4EF6\u4E0B\u8F7D\u4E0B\u6765</strong>\uFF0C\u4E4B\u540E<strong>\u518D\u52A0\u8F7D\u8FD0\u884C</strong>\uFF1B</li><li>\u90A3\u4E48\u91C7\u7528<strong>\u540C\u6B65\u7684\u5C31\u610F\u5473\u7740\u540E\u7EED\u7684js\u4EE3\u7801\u90FD\u65E0\u6CD5\u6B63\u5E38\u8FD0\u884C</strong>\uFF0C\u5373\u4F7F\u662F<strong>\u4E00\u4E9B\u7B80\u5355\u7684 DOM \u64CD\u4F5C</strong>\uFF1B</li></ul><p><em>\u6240\u4EE5\u5728\u6D4F\u89C8\u5668\u4E2D\uFF0C\u6211\u4EEC\u901A\u5E38\u4E0D\u4F7F\u7528CommonJS\u89C4\u8303\uFF1A</em></p><ul><li>\u5F53\u7136\u5728 webpack \u4E2D\u4F7F\u7528 CommonJS \u662F\u53E6\u5916\u4E00\u56DE\u4E8B\uFF1B</li><li>\u56E0\u4E3A\u5B83\u4F1A\u5C06\u6211\u4EEC\u7684\u4EE3\u7801\u8F6C\u6210\u6D4F\u89C8\u5668\u53EF\u4EE5\u76F4\u63A5\u6267\u884C\u7684\u4EE3\u7801\uFF1B</li></ul><p><em>\u5728\u65E9\u671F\u4E3A\u4E86\u53EF\u4EE5\u5728\u6D4F\u89C8\u5668\u4E2D\u4F7F\u7528\u6A21\u5757\u5316\uFF0C\u901A\u5E38\u4F1A\u91C7\u7528AMD\u6216CMD\uFF1A</em></p><ul><li>\u4F46\u662F\u76EE\u524D\u4E00\u65B9\u9762\u73B0\u4EE3\u7684\u6D4F\u89C8\u5668<strong>\u5DF2\u7ECF\u652F\u6301ES Modules</strong>\uFF0C\u53E6\u4E00\u65B9\u9762\u501F\u52A9\u4E8E webpack \u7B49\u5DE5\u5177\u53EF\u4EE5<strong>\u5B9E\u73B0\u5BF9CommonJS\u6216\u8005ES Module\u4EE3\u7801\u7684\u8F6C\u6362</strong>\uFF1B</li><li><strong>AMD \u548C CMD \u5DF2\u7ECF\u4F7F\u7528\u975E\u5E38\u5C11\u4E86</strong>\uFF0C\u6240\u4EE5\u8FD9\u91CC\u6211\u4EEC\u8FDB\u884C\u7B80\u5355\u7684\u6F14\u7EC3</li></ul><h2 id="amd-\u89C4\u8303" tabindex="-1"><a class="header-anchor" href="#amd-\u89C4\u8303" aria-hidden="true">#</a> AMD \u89C4\u8303</h2><p>AMD\u4E3B\u8981\u662F\u5E94\u7528\u4E8E\u6D4F\u89C8\u5668\u7684\u4E00\u79CD\u6A21\u5757\u5316\u89C4\u8303\uFF1A</p><ul><li>AMD \u662F Asynchronous Module Definition\uFF08\u5F02\u6B65\u6A21\u5757\u5B9A\u4E49\uFF09\u7684\u7F29\u5199\uFF1B</li><li>\u5B83\u91C7\u7528\u7684\u662F<strong>\u5F02\u6B65\u52A0\u8F7D\u6A21\u5757</strong>\uFF1B</li><li>\u4E8B\u5B9E\u4E0A AMD \u7684\u89C4\u8303\u8FD8\u8981\u65E9\u4E8E CommonJS\uFF0C\u4F46\u662F CommonJS \u76EE\u524D\u4F9D\u7136\u5728\u88AB\u4F7F\u7528\uFF0C\u800C AMD \u4F7F\u7528\u7684\u8F83\u5C11\u4E86\uFF1B</li></ul><p>\u89C4\u8303\u53EA\u662F\u5B9A\u4E49\u4EE3\u7801\u7684\u5E94\u8BE5\u5982\u4F55\u53BB\u7F16\u5199\uFF0C\u53EA\u6709\u6709\u4E86\u5177\u4F53\u7684\u5B9E\u73B0\u624D\u80FD\u88AB\u5E94\u7528\uFF1A</p><ul><li>AMD \u5B9E\u73B0\u7684\u6BD4\u8F83\u5E38\u7528\u7684\u5E93\u662F require.js \u548C curl.js\uFF1B</li></ul><h2 id="cmd-\u89C4\u8303" tabindex="-1"><a class="header-anchor" href="#cmd-\u89C4\u8303" aria-hidden="true">#</a> CMD \u89C4\u8303</h2><p>CMD\u89C4\u8303\u4E5F\u662F\u5E94\u7528\u4E8E\u6D4F\u89C8\u5668\u7684\u4E00\u79CD\u6A21\u5757\u5316\u89C4\u8303\uFF1A</p><ul><li>CMD \u662FCommon Module Definition\uFF08\u901A\u7528\u6A21\u5757\u5B9A\u4E49\uFF09\u7684\u7F29\u5199\uFF1B</li><li>\u5B83\u4E5F\u91C7\u7528\u4E86\u5F02\u6B65\u52A0\u8F7D\u6A21\u5757\uFF0C\u4F46\u662F\u5B83\u5C06CommonJS\u7684\u4F18\u70B9\u5438\u6536\u4E86\u8FC7\u6765\uFF1B</li><li>\u4F46\u662F\u76EE\u524DCMD\u4F7F\u7528\u4E5F\u975E\u5E38\u5C11\u4E86\uFF1B</li></ul><p>CMD\u4E5F\u6709\u81EA\u5DF1\u6BD4\u8F83\u4F18\u79C0\u7684\u5B9E\u73B0\u65B9\u6848\uFF1A</p><ul><li>SeaJS</li></ul><h2 id="es-module" tabindex="-1"><a class="header-anchor" href="#es-module" aria-hidden="true">#</a> ES Module</h2><p>ES Module \u548C CommonJS \u7684\u6A21\u5757\u5316\u6709\u4E00\u4E9B\u4E0D\u540C\u4E4B\u5904\uFF1A</p><ul><li>\u4E00\u65B9\u9762\u5B83\u4F7F\u7528\u4E86 import \u548C export \u5173\u952E\u5B57\uFF1B</li><li>\u53E6\u4E00\u65B9\u9762\u5B83\u91C7\u7528\u7F16\u8BD1\u671F\u7684\u9759\u6001\u5206\u6790\uFF0C\u5E76\u4E14\u4E5F\u52A0\u5165\u4E86\u52A8\u6001\u5F15\u7528\u7684\u65B9\u5F0F\uFF1B</li></ul><p>ES Module \u6A21\u5757\u91C7\u7528 export \u548C import \u5173\u952E\u5B57\u6765\u5B9E\u73B0\u6A21\u5757\u5316\uFF1A</p><ul><li>export \u8D1F\u8D23\u5C06\u6A21\u5757\u5185\u7684\u5185\u5BB9\u5BFC\u51FA\uFF1B</li><li>import \u8D1F\u8D23\u4ECE\u5176\u4ED6\u6A21\u5757\u5BFC\u5165\u5185\u5BB9\uFF1B</li></ul><p>\u4E86\u89E3\uFF1A\u91C7\u7528 ES Module \u5C06\u81EA\u52A8\u91C7\u7528\u4E25\u683C\u6A21\u5F0F\uFF1A<strong>use strict</strong></p><h3 id="export-\u5173\u952E\u5B57" tabindex="-1"><a class="header-anchor" href="#export-\u5173\u952E\u5B57" aria-hidden="true">#</a> export \u5173\u952E\u5B57</h3><p>export \u5173\u952E\u5B57\u5C06\u4E00\u4E2A\u6A21\u5757\u4E2D\u7684\u53D8\u91CF\u3001\u51FD\u6570\u3001\u7C7B\u7B49\u5BFC\u51FA</p><ul><li>\u65B9\u5F0F\u4E00\uFF1A\u5728\u8BED\u53E5\u58F0\u660E\u7684\u524D\u9762\u76F4\u63A5\u52A0\u4E0A export \u5173\u952E\u5B57</li><li>\u65B9\u5F0F\u4E8C\uFF1A\u5C06\u6240\u6709\u9700\u8981\u5BFC\u51FA\u7684\u6807\u8BC6\u7B26\uFF0C\u653E\u5230 export \u540E\u9762\u7684 {}\u4E2D <ul><li>\u6CE8\u610F\uFF1A\u8FD9\u91CC\u7684 {} \u91CC\u9762\u4E0D\u662FES6\u7684\u5BF9\u8C61\u5B57\u9762\u91CF\u7684\u589E\u5F3A\u5199\u6CD5\uFF0C{}\u4E5F\u4E0D\u662F\u8868\u793A\u4E00\u4E2A\u5BF9\u8C61\u7684\uFF1B</li><li>\u6240\u4EE5\uFF1A export {name: name}\uFF0C\u662F\u9519\u8BEF\u7684\u5199\u6CD5\uFF1B</li></ul></li><li>\u65B9\u5F0F\u4E09\uFF1A\u5BFC\u51FA\u65F6\u7ED9\u6807\u8BC6\u7B26\u8D77\u4E00\u4E2A\u522B\u540D</li></ul><p><img src="'+d+'" alt="\u56FE\u7247"></p><h3 id="import-\u5173\u952E\u5B57" tabindex="-1"><a class="header-anchor" href="#import-\u5173\u952E\u5B57" aria-hidden="true">#</a> import \u5173\u952E\u5B57</h3><p>\u5BFC\u5165\u5185\u5BB9\u7684\u65B9\u5F0F\u4E5F\u6709\u591A\u79CD\uFF1A</p><ul><li>\u65B9\u5F0F\u4E00\uFF1Aimport { \u6807\u8BC6\u7B26\u5217\u8868 } from &#39;\u6A21\u5757&#39;\uFF1B <ul><li>\u6CE8\u610F\uFF1A\u8FD9\u91CC\u7684 {} \u4E5F\u4E0D\u662F\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u91CC\u9762\u53EA\u662F\u5B58\u653E\u5BFC\u5165\u7684\u6807\u8BC6\u7B26\u5217\u8868\u5185\u5BB9\uFF1B</li></ul></li><li>\u65B9\u5F0F\u4E8C\uFF1A\u5BFC\u5165\u65F6\u7ED9\u6807\u8BC6\u7B26\u8D77\u522B\u540D</li><li>\u65B9\u5F0F\u4E09\uFF1A\u901A\u8FC7 * \u5C06\u6A21\u5757\u529F\u80FD\u653E\u5230\u4E00\u4E2A\u6A21\u5757\u529F\u80FD\u5BF9\u8C61\uFF08a module object\uFF09\u4E0A</li></ul><p><img src="'+m+'" alt="\u56FE\u7247"></p><h3 id="export-\u548C-import-\u7ED3\u5408\u4F7F\u7528" tabindex="-1"><a class="header-anchor" href="#export-\u548C-import-\u7ED3\u5408\u4F7F\u7528" aria-hidden="true">#</a> export \u548C import \u7ED3\u5408\u4F7F\u7528</h3><p>\u8865\u5145\uFF1Aexport \u548C import \u53EF\u4EE5\u7ED3\u5408\u4F7F\u7528</p><p>\u4E3A\u4EC0\u4E48\u8981\u8FD9\u6837\u505A\u5462\uFF1F</p><ul><li>\u5728\u5F00\u53D1\u548C\u5C01\u88C5\u4E00\u4E2A\u529F\u80FD\u5E93\u65F6\uFF0C\u901A\u5E38\u6211\u4EEC\u5E0C\u671B\u5C06\u66B4\u9732\u7684\u6240\u6709\u63A5\u53E3\u653E\u5230\u4E00\u4E2A\u6587\u4EF6\u4E2D\uFF1B</li><li>\u8FD9\u6837\u65B9\u4FBF\u6307\u5B9A\u7EDF\u4E00\u7684\u63A5\u53E3\u89C4\u8303\uFF0C\u4E5F\u65B9\u4FBF\u9605\u8BFB\uFF1B</li><li>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u4F7F\u7528 export \u548C import \u7ED3\u5408\u4F7F\u7528</li></ul><p><img src="'+u+'" alt="\u56FE\u7247"></p><h3 id="default-\u7528\u6CD5" tabindex="-1"><a class="header-anchor" href="#default-\u7528\u6CD5" aria-hidden="true">#</a> default \u7528\u6CD5</h3><p>\u524D\u9762\u7684\u5BFC\u51FA\u529F\u80FD\u90FD\u662F\u6709\u540D\u5B57\u7684\u5BFC\u51FA\uFF08named exports\uFF09\uFF1A</p><ul><li>\u5728\u5BFC\u51FA export \u65F6\u6307\u5B9A\u4E86\u540D\u5B57\uFF1B</li><li>\u5728\u5BFC\u5165 import \u65F6\u9700\u8981\u77E5\u9053\u5177\u4F53\u7684\u540D\u5B57\uFF1B</li></ul><p>\u8FD8\u6709\u4E00\u79CD\u5BFC\u51FA\u53EB\u505A\u9ED8\u8BA4\u5BFC\u51FA\uFF08default export\uFF09</p><ul><li>\u9ED8\u8BA4\u5BFC\u51FA export \u65F6\u53EF\u4EE5\u4E0D\u9700\u8981\u6307\u5B9A\u540D\u5B57\uFF1B</li><li>\u5728\u5BFC\u5165\u65F6\u4E0D\u9700\u8981\u4F7F\u7528 {}\uFF0C\u5E76\u4E14\u53EF\u4EE5\u81EA\u5DF1\u6765\u6307\u5B9A\u540D\u5B57\uFF1B</li><li>\u5B83\u4E5F\u65B9\u4FBF\u6211\u4EEC\u548C\u73B0\u6709\u7684 CommonJS \u7B49\u89C4\u8303\u76F8\u4E92\u64CD\u4F5C\uFF1B</li></ul><p>\u6CE8\u610F\uFF1A\u5728\u4E00\u4E2A\u6A21\u5757\u4E2D\uFF0C<strong>\u53EA\u80FD\u6709\u4E00\u4E2A</strong>\u9ED8\u8BA4\u5BFC\u51FA\uFF08default export\uFF09\uFF1B <img src="'+g+'" alt="\u56FE\u7247"></p><h3 id="import-\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#import-\u51FD\u6570" aria-hidden="true">#</a> import \u51FD\u6570</h3><p>\u901A\u8FC7 import \u52A0\u8F7D\u4E00\u4E2A\u6A21\u5757\uFF0C\u662F\u4E0D\u53EF\u4EE5\u5728\u5176\u653E\u5230\u903B\u8F91\u4EE3\u7801\u4E2D\u7684\uFF0C\u6BD4\u5982\uFF1A</p><p>\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0\u8FD9\u4E2A\u60C5\u51B5\u5462\uFF1F</p><ul><li>\u8FD9\u662F\u56E0\u4E3AES Module\u5728\u88ABJS\u5F15\u64CE\u89E3\u6790\u65F6\uFF0C\u5C31\u5FC5\u987B\u77E5\u9053\u5B83\u7684\u4F9D\u8D56\u5173\u7CFB\uFF1B</li><li>\u7531\u4E8E\u8FD9\u4E2A\u65F6\u5019js\u4EE3\u7801\u6CA1\u6709\u4EFB\u4F55\u7684\u8FD0\u884C\uFF0C\u6240\u4EE5\u65E0\u6CD5\u5728\u8FDB\u884C\u7C7B\u4F3C\u4E8Eif\u5224\u65AD\u4E2D\u6839\u636E\u4EE3\u7801\u7684\u6267\u884C\u60C5\u51B5\uFF1B</li><li>\u751A\u81F3\u4E0B\u9762\u7684\u8FD9\u79CD\u5199\u6CD5\u4E5F\u662F\u9519\u8BEF\u7684\uFF1A\u56E0\u4E3A\u6211\u4EEC\u5FC5\u987B\u5230\u8FD0\u884C\u65F6\u80FD\u786E\u5B9Apath\u7684\u503C\uFF1B</li></ul><p>\u4F46\u662F\u67D0\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u786E\u786E\u5B9E\u5B9E\u5E0C\u671B\u52A8\u6001\u7684\u6765\u52A0\u8F7D\u67D0\u4E00\u4E2A\u6A21\u5757\uFF1A</p><ul><li>\u5982\u679C\u6839\u636E\u4E0D\u540C\u7684\u6761\u4EF6\uFF0C\u52A8\u6001\u6765\u9009\u62E9\u52A0\u8F7D\u6A21\u5757\u7684\u8DEF\u5F84\uFF1B</li><li>\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u9700\u8981\u4F7F\u7528 import() \u51FD\u6570\u6765\u52A8\u6001\u52A0\u8F7D\uFF1B</li></ul><p><img src="'+h+'" alt="\u56FE\u7247"></p><h3 id="import-meta" tabindex="-1"><a class="header-anchor" href="#import-meta" aria-hidden="true">#</a> i<wbr>mport.meta</h3><p>i<wbr>mport.meta ( \u4E0A\u9762\u7684\u56FE\u4E2D )\u662F\u4E00\u4E2A\u7ED9 JavaScript \u6A21\u5757\u66B4\u9732\u7279\u5B9A\u4E0A\u4E0B\u6587\u7684\u5143\u6570\u636E\u5C5E\u6027\u7684\u5BF9\u8C61\u3002</p><ul><li>\u5B83\u5305\u542B\u4E86\u8FD9\u4E2A\u6A21\u5757\u7684\u4FE1\u606F\uFF0C\u6BD4\u5982\u8BF4\u8FD9\u4E2A\u6A21\u5757\u7684URL\uFF1B</li><li>\u5728ES11\uFF08ES2020\uFF09\u4E2D\u65B0\u589E\u7684\u7279\u6027\uFF1B</li></ul><h3 id="esm-\u548C-cjs-\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#esm-\u548C-cjs-\u5BF9\u6BD4" aria-hidden="true">#</a> ESM \u548C CJS \u5BF9\u6BD4</h3><p>CommonJS \u5BFC\u51FA\u7684\u662F\u503C\u5F97\u62F7\u8D1D , ESM \u5BFC\u51FA\u7684\u662F\u503C\u5F97\u5F15\u7528</p><p>CommonJS \u80FD\u5BFC\u51FA\u4E00\u4E2A\u5BF9\u8C61 , ESM \u53EF\u4EE5\u5230\u5904\u591A\u4E2A\u503C</p><p>CommonJS \u662F\u540C\u6B65\u52A0\u8F7D , ESM \u662F\u5F02\u6B65\u52A0\u8F7D</p>',117);function J(A,f){return x}var C=i(c,[["render",J],["__file","mokuaihua.html.vue"]]);export{C as default};
