import{_ as e,d}from"./app.fcb85f29.js";var o="/myBlog-vp/assets/xxrr1.1586f95e.png",c="/myBlog-vp/assets/xxrr2.b2112e89.png",r="/myBlog-vp/assets/xxrr3.20ba681d.png",i="/myBlog-vp/assets/xxrr4.56ea7489.png",a="/myBlog-vp/assets/df1.017c3c6a.png";const l={},t=d('<h1 id="\u6D4F\u89C8\u5668\u7684\u6E32\u67D3\u539F\u7406" tabindex="-1"><a class="header-anchor" href="#\u6D4F\u89C8\u5668\u7684\u6E32\u67D3\u539F\u7406" aria-hidden="true">#</a> \u6D4F\u89C8\u5668\u7684\u6E32\u67D3\u539F\u7406</h1><h2 id="\u6E32\u67D3\u9875\u9762\u7684\u8BE6\u7EC6\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u6E32\u67D3\u9875\u9762\u7684\u8BE6\u7EC6\u6D41\u7A0B" aria-hidden="true">#</a> \u6E32\u67D3\u9875\u9762\u7684\u8BE6\u7EC6\u6D41\u7A0B</h2><p><img src="'+o+'" alt="\u56FE\u7247"></p><h3 id="\u89E3\u6790\u4E00-html\u89E3\u6790\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u89E3\u6790\u4E00-html\u89E3\u6790\u8FC7\u7A0B" aria-hidden="true">#</a> \u89E3\u6790\u4E00 - HTML\u89E3\u6790\u8FC7\u7A0B</h3><p>\u56E0\u4E3A\u9ED8\u8BA4\u60C5\u51B5\u670D\u52A1\u5668\u4F1A\u7ED9\u6D4F\u89C8\u5668\u8FD4\u56DE <code>index.html</code> \u6587\u4EF6, \u6240\u4EE5\u89E3\u6790 <code>HTML</code> \u662F\u6240\u6709\u6B65\u9AA4\u7684\u5F00\u59CB</p><p>\u89E3\u6790 <code>HTML</code> \u4F1A\u6784\u5EFA <code>DOM tree</code><img src="'+c+'" alt="\u56FE\u7247"></p><h3 id="\u89E3\u6790\u4E8C-\u2013-\u751F\u6210css\u89C4\u5219" tabindex="-1"><a class="header-anchor" href="#\u89E3\u6790\u4E8C-\u2013-\u751F\u6210css\u89C4\u5219" aria-hidden="true">#</a> \u89E3\u6790\u4E8C \u2013 \u751F\u6210CSS\u89C4\u5219</h3><p>\u5728\u89E3\u6790\u8FC7\u7A0B\u4E2D, \u5982\u679C\u4F1A\u9047\u5230 <code>CSS</code> \u7684 <code>link</code>\u5143\u7D20, \u90A3\u4E48\u4F1A\u7531\u6D4F\u89C8\u5668\u4E0B\u8F7D\u5BF9\u5E94\u7684CSS\u6587\u4EF6</p><ul><li>\u6CE8\u610F: \u4E0B\u8F7D <code>CSS</code> \u6587\u4EF6\u662F\u4E0D\u4F1A\u5F71\u54CD <code>DOM</code> \u7684\u89E3\u6790</li></ul><p>\u6D4F\u89C8\u5668\u4E0B\u8F7D\u5B8C <code>CSS</code> \u6587\u4EF6\u4E4B\u540E , \u4F1A\u5BF9 <code>CSS</code> \u8FDB\u884C\u89E3\u6790, <strong>\u89E3\u6790\u51FA\u5BF9\u5E94\u7684\u89C4\u5219\u6811</strong></p><ul><li>\u6211\u4EEC\u53EF\u4EE5\u79F0\u4E4B\u4E3A <code>CSSOM(CSS Object Model , CSS \u5BF9\u8C61\u6A21\u578B)</code></li></ul><p><img src="'+r+'" alt="\u56FE\u7247"></p><h3 id="\u89E3\u6790\u4E09-\u6784\u5EFA-render-tree" tabindex="-1"><a class="header-anchor" href="#\u89E3\u6790\u4E09-\u6784\u5EFA-render-tree" aria-hidden="true">#</a> \u89E3\u6790\u4E09 - \u6784\u5EFA render tree</h3><p><strong>\u5F53\u6709\u4E86 <code>DOM tree</code> \u548C <code>CSSOM tree</code> \u4E4B\u540E, \u5C31\u53EF\u4EE5\u4E24\u4E2A\u7ED3\u5408\u6765\u6784\u5EFA <code>Render tree</code></strong><img src="'+i+'" alt="\u56FE\u7247"></p><p>\u6CE8\u610F1 : <strong><code>link</code> \u5143\u7D20\u4E0D\u4F1A\u963B\u585E <code>DOM tree</code> \u7684\u6784\u5EFA\u8FC7\u7A0B, \u4F46\u662F\u4F1A\u963B\u585E <code>Render tree</code> \u7684\u6784\u5EFA\u8FC7\u7A0B</strong></p><ul><li>\u56E0\u4E3A <code>Render tree</code> \u5728\u6784\u5EFA\u65F6, \u9700\u8981\u5BF9\u5E94\u7684 <code>CSSOM tree</code></li></ul><p>\u6CE8\u610F2 : <strong><code>Render tree</code> \u548C <code>DOM tree</code> \u5E76\u4E0D\u662F\u4E00\u4E00\u5BF9\u5E94\u7684\u5173\u7CFB</strong>, \u6BD4\u5982 <code>display:none</code> \u7684\u5143\u7D20, \u538B\u6839\u4E0D\u5B58\u5728\u5728 <code>Render tree</code> \u91CC</p><h3 id="\u89E3\u6790\u56DB-\u2013-\u5E03\u5C40-layout-\u548C\u7ED8\u5236-paint" tabindex="-1"><a class="header-anchor" href="#\u89E3\u6790\u56DB-\u2013-\u5E03\u5C40-layout-\u548C\u7ED8\u5236-paint" aria-hidden="true">#</a> \u89E3\u6790\u56DB \u2013 \u5E03\u5C40\uFF08layout\uFF09\u548C\u7ED8\u5236\uFF08Paint\uFF09</h3><p><strong>\u7B2C\u56DB\u6B65\u662F\u5728\u6E32\u67D3\u6811<code>\uFF08Render Tree\uFF09</code>\u4E0A\u8FD0\u884C\u5E03\u5C40<code>\uFF08Layout\uFF09</code>\u4EE5\u8BA1\u7B97\u6BCF\u4E2A\u8282\u70B9\u7684\u51E0\u4F55\u4F53</strong></p><ul><li>\u6E32\u67D3\u6811\u4F1A\u8868\u793A\u663E\u793A\u54EA\u4E9B\u8282\u70B9\u4EE5\u53CA\u5176\u4ED6\u6837\u5F0F\uFF0C\u4F46\u662F\u4E0D\u8868\u793A\u6BCF\u4E2A\u8282\u70B9\u7684\u5C3A\u5BF8\u3001\u4F4D\u7F6E\u7B49\u4FE1\u606F\uFF1B</li><li>\u5E03\u5C40\u662F\u786E\u5B9A\u5448\u73B0\u6811\u4E2D\u6240\u6709\u8282\u70B9\u7684\u5BBD\u5EA6\u3001\u9AD8\u5EA6\u548C\u4F4D\u7F6E\u4FE1\u606F\uFF1B</li></ul><p><strong>\u7B2C\u4E94\u6B65\u662F\u5C06\u6BCF\u4E2A\u8282\u70B9\u7ED8\u5236\uFF08Paint\uFF09\u5230\u5C4F\u5E55\u4E0A</strong></p><ul><li>\u5728\u7ED8\u5236\u9636\u6BB5\uFF0C\u6D4F\u89C8\u5668\u5C06\u5E03\u5C40\u9636\u6BB5\u8BA1\u7B97\u7684\u6BCF\u4E2Aframe\u8F6C\u4E3A\u5C4F\u5E55\u4E0A\u5B9E\u9645\u7684\u50CF\u7D20\u70B9\uFF1B</li><li>\u5305\u62EC\u5C06\u5143\u7D20\u7684\u53EF\u89C1\u90E8\u5206\u8FDB\u884C\u7ED8\u5236\uFF0C\u6BD4\u5982\u6587\u672C\u3001\u989C\u8272\u3001\u8FB9\u6846\u3001\u9634\u5F71\u3001\u66FF\u6362\u5143\u7D20\uFF08\u6BD4\u5982img\uFF09</li></ul><h2 id="\u56DE\u6D41\u548C\u91CD\u7ED8" tabindex="-1"><a class="header-anchor" href="#\u56DE\u6D41\u548C\u91CD\u7ED8" aria-hidden="true">#</a> \u56DE\u6D41\u548C\u91CD\u7ED8</h2><ol><li><strong>\u7406\u89E3\u56DE\u6D41 reflow, (\u4E5F\u53EF\u4EE5\u79F0\u4E4B\u4E3A\u91CD\u6392)</strong></li></ol><ul><li>\u7B2C\u4E00\u6B21\u786E\u5B9A\u8282\u70B9\u7684\u5927\u5C0F\u548C\u4F4D\u7F6E, \u79F0\u4E4B\u4E3A\u5E03\u5C40(layout)</li><li>\u4E4B\u540E\u5E03\u5C40\u53D1\u751F\u4E86\u6539\u53D8, \u5BF9\u8282\u70B9\u7684\u5927\u5C0F\u548C\u4F4D\u7F6E\u91CD\u65B0\u8FDB\u884C\u8BA1\u7B97\u79F0\u4E4B\u4E3A\u56DE\u6D41</li></ul><p><strong>\u4EC0\u4E48\u60C5\u51B5\u4E0B\u5F15\u8D77\u56DE\u6D41?</strong></p><ul><li>\u6BD4\u5982 <code>DOM</code> \u7ED3\u6784\u53D1\u751F\u4E86\u53D8\u5316 (\u6DFB\u52A0\u65B0\u7684\u8282\u70B9, \u79FB\u9664\u8282\u70B9)</li><li>\u6BD4\u5982\u6539\u53D8\u4E86\u5E03\u5C40 (\u4FEE\u6539\u4E86 <code>width, height , padding , font-size</code> \u7B49\u7B49)</li><li>\u7A97\u53E3 <code>resize</code> \u4E8B\u4EF6</li><li>\u8C03\u7528\u4E86 <code>getComputedStyle</code> \u65B9\u6CD5\u83B7\u53D6\u5C3A\u5BF8,\u4F4D\u7F6E\u4FE1\u606F</li></ul><ol start="2"><li><strong>\u7406\u89E3\u91CD\u7ED8(repaint)</strong></li></ol><ul><li>\u7B2C\u4E00\u6B21\u6E32\u67D3\u5185\u5BB9\u79F0\u4E4B\u4E3A\u7ED8\u5236(paint)</li><li>\u4E4B\u540E\u91CD\u65B0\u6E32\u67D3\u79F0\u4E4B\u4E3A\u91CD\u7ED8</li></ul><p><strong>\u4EC0\u4E48\u60C5\u51B5\u4E0B\u5F15\u8D77\u91CD\u7ED8</strong></p><ul><li>\u6BD4\u5982\u4FEE\u6539\u4E86\u80CC\u666F\u8272, \u6587\u5B57\u989C\u8272, \u8FB9\u6846\u989C\u8272, \u6837\u5F0F\u7B49</li></ul><div class="custom-container warning"><p class="custom-container-title">WARNING</p><p><em>\u56DE\u6D41\u4E00\u5B9A\u4F1A\u5F15\u8D77\u91CD\u7ED8\uFF0C\u6240\u4EE5\u56DE\u6D41\u662F\u4E00\u4EF6\u5F88\u6D88\u8017\u6027\u80FD\u7684\u4E8B\u60C5\u3002</em></p></div><p>\u6240\u4EE5\u5728\u5F00\u53D1\u4E2D\u5E94\u8BE5\u907F\u514D\u53D1\u751F\u56DE\u6D41</p><ol><li>\u4FEE\u6539\u6837\u5F0F\u65F6\u5C3D\u91CF\u4E00\u6B21\u6027\u4FEE\u6539</li></ol><ul><li>\u6BD4\u5982\u901A\u8FC7\u6DFB\u52A0 class \u4FEE\u6539</li></ul><ol start="2"><li>\u5C3D\u91CF\u907F\u514D\u9891\u7E41\u7684\u64CD\u4F5C <code>DOM</code></li></ol><ul><li>\u6211\u4EEC\u53EF\u4EE5\u5728\u4E00\u4E2A <code>DocumentFragment</code> \u6216\u8005\u7236\u5143\u7D20\u4E2D\u5C06\u8981\u64CD\u4F5C\u7684DOM\u64CD\u4F5C\u5B8C\u6210\uFF0C\u518D\u4E00\u6B21\u6027\u7684\u64CD\u4F5C\uFF1B</li></ul><ol start="3"><li>\u5C3D\u91CF\u907F\u514D\u901A\u8FC7 <code>getComputedStyle</code> \u83B7\u53D6\u5C3A\u5BF8\u3001\u4F4D\u7F6E\u7B49\u4FE1\u606F\uFF1B</li><li>\u5BF9\u67D0\u4E9B\u5143\u7D20\u4F7F\u7528 <code>position</code> \u7684 <strong>absolute\u6216\u8005fixed</strong></li></ol><ul><li>\u5E76\u4E0D\u662F\u4E0D\u4F1A\u5F15\u8D77\u56DE\u6D41\uFF0C\u800C\u662F\u5F00\u9500\u76F8\u5BF9\u8F83\u5C0F\uFF0C\u4E0D\u4F1A\u5BF9\u5176\u4ED6\u5143\u7D20\u9020\u6210\u5F71\u54CD</li></ul><h2 id="script-\u5143\u7D20\u548C\u9875\u9762\u89E3\u6790\u7684\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#script-\u5143\u7D20\u548C\u9875\u9762\u89E3\u6790\u7684\u5173\u7CFB" aria-hidden="true">#</a> script \u5143\u7D20\u548C\u9875\u9762\u89E3\u6790\u7684\u5173\u7CFB</h2><ul><li>\u4E8B\u5B9E\u4E0A, \u6D4F\u89C8\u5668\u5728\u89E3\u6790 <code>HTML</code> \u7684\u8FC7\u7A0B\u4E2D, \u5982\u679C\u9047\u5230\u4E86 <code>script</code> \u6807\u7B7E\u662F\u4E0D\u80FD\u7EE7\u7EED\u6784\u5EFA <code>DOM</code> \u6811\u7684</li><li>\u4ED6\u4F1A\u505C\u6B62\u7EE7\u7EED\u6784\u5EFA, \u9996\u5148\u4E0B\u8F7D <code>javascript</code> \u7684\u4EE3\u7801, \u5E76\u4E14\u6267\u884C javascript \u811A\u672C</li><li>\u53EA\u6709\u7B49\u5230 <code>javascript</code> \u811A\u672C\u6267\u884C\u5B8C\u6210\u540E, \u624D\u4F1A\u7EE7\u7EED\u89E3\u6790 <code>HTML</code> , \u6784\u5EFA <code>DOM tree</code></li></ul><p>\u4E3A\u4EC0\u4E48\u8981\u8FD9\u6837\u505A\u5462\uFF1F</p><ul><li>\u8FD9\u662F\u56E0\u4E3A <code>javascript</code> \u7684\u4F5C\u7528\u4E4B\u4E00\u5C31\u662F\u64CD\u4F5CDOM, \u5E76\u4E14\u53EF\u4EE5\u4FEE\u6539 <code>DOM</code></li><li>\u5982\u679C\u7B49\u5230DOM\u6811\u6784\u5EFA\u5B8C\u6210\u5E76\u6E32\u67D3\u518D\u6267\u884C <code>javascript</code> , \u4F1A\u9020\u6210\u4E25\u91CD\u7684\u56DE\u6D41\u548C\u91CD\u7ED8, \u5F71\u54CD\u9875\u9762\u7684\u6027\u80FD</li><li>\u6240\u4EE5\u9047\u5230 <code>script</code> \u6807\u7B7E, \u4F1A\u4F18\u5148\u4E0B\u8F7D\u548C\u6267\u884C <code>javascript</code> \u4EE3\u7801, \u518D\u6784\u5EFA <code>DOM</code> \u6811</li></ul><p>\u4F46\u662F\u8FD9\u4E2A\u4E5F\u5F80\u5F80\u4F1A\u5E26\u6765\u65B0\u7684\u95EE\u9898\uFF0C\u7279\u522B\u662F\u73B0\u4EE3\u9875\u9762\u5F00\u53D1\u4E2D\uFF1A</p><ul><li>\u5728\u76EE\u524D\u7684\u5F00\u53D1\u6A21\u5F0F\u4E2D (Vue, React), \u811A\u672C\u5F80\u5F80\u6BD4 HTML \u9875\u9762\u66F4&quot;\u91CD&quot;, \u5904\u7406\u65F6\u95F4\u8FD8\u6BD4\u8F83\u957F</li><li>\u6240\u4EE5\u4F1A\u9020\u6210\u9875\u9762\u7684\u89E3\u6790\u963B\u585E, \u5728\u811A\u672C\u4E0B\u8F7D\u5E76\u6267\u884C\u4E4B\u524D, \u7528\u6237\u5728\u754C\u9762\u4EC0\u4E48\u90FD\u770B\u4E0D\u5230</li></ul><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0Cscript\u5143\u7D20\u7ED9\u6211\u4EEC\u63D0\u4F9B\u4E86\u4E24\u4E2A\u5C5E\u6027\uFF08attribute\uFF09\uFF1A<code>defer \u548C async</code></p><h3 id="defer-\u5C5E\u6027" tabindex="-1"><a class="header-anchor" href="#defer-\u5C5E\u6027" aria-hidden="true">#</a> defer \u5C5E\u6027</h3><p><code>defer</code> \u5C5E\u6027\u544A\u8BC9\u6D4F\u89C8\u5668, <strong>\u4E0D\u8981\u7B49\u5F85\u811A\u672C\u7684\u4E0B\u8F7D, \u800C\u7EE7\u7EED\u89E3\u6790 <code>HTML</code>, \u6784\u5EFA <code>DOM tree</code></strong></p><ul><li>\u811A\u672C\u4F1A\u7531\u6D4F\u89C8\u5668\u8FDB\u884C\u4E0B\u8F7D, \u4F46\u662F\u4E0D\u4F1A\u963B\u585E <code>DOM tree</code> \u7684\u6784\u5EFA</li><li>\u5982\u679C\u811A\u672C\u63D0\u524D\u4E0B\u8F7D\u597D\u4E86, \u4ED6\u4F1A\u7B49\u5F85 <code>DOM Tree</code> \u7684\u6784\u5EFA\u5B8C\u6210, \u5728 <code>DOMContentLoaded</code> \u4E8B\u4EF6\u4E4B\u524D\u518D\u6267\u884C <code>defer</code> \u4E2D\u7684\u4EE3\u7801</li></ul><p>\u6240\u4EE5 <code>DOMContentLoaded</code> \u603B\u662F\u4F1A\u7B49\u5F85 <code>defer</code> \u4E2D\u7684\u4EE3\u7801\u5148\u6267\u884C\u5B8C\u6210 <img src="'+a+'" alt="\u56FE\u7247"></p><p>\u53E6\u5916\u591A\u4E2A\u5E26 defer \u7684\u811A\u672C\u53EF\u4EE5\u4FDD\u8BC1\u987A\u5E8F\u6765\u6267\u884C\u7684</p><p>\u4ECE\u67D0\u79CD\u89D2\u5EA6\u6765\u8BF4, defer \u53EF\u4EE5\u63D0\u9AD8\u9875\u9762\u7684\u6027\u80FD, \u5E76\u4E14\u63A8\u8350\u653E\u5230 head \u5143\u7D20\u4E2D</p><p>\u6CE8\u610F\uFF1Adefer \u4EC5\u9002\u7528\u4E8E\u5916\u90E8\u811A\u672C\uFF0C\u5BF9\u4E8Escript\u9ED8\u8BA4\u5185\u5BB9\u4F1A\u88AB\u5FFD\u7565\u3002</p><h3 id="async-\u5C5E\u6027" tabindex="-1"><a class="header-anchor" href="#async-\u5C5E\u6027" aria-hidden="true">#</a> async \u5C5E\u6027</h3><p><code>async</code> \u7279\u6027\u4E0E <code>defer</code> \u6709\u4E9B\u7C7B\u4F3C\uFF0C\u5B83\u4E5F\u80FD\u591F\u8BA9\u811A\u672C\u4E0D\u963B\u585E\u9875\u9762\u3002</p><p><strong>async \u8BA9\u4E00\u4E2A\u811A\u672C\u5B8C\u5168\u72EC\u7ACB</strong></p><ul><li>\u6D4F\u89C8\u5668\u4E0D\u4F1A\u56E0 <code>async</code> \u811A\u672C\u800C\u963B\u585E(\u4E0E <code>defer</code> \u7C7B\u4F3C)</li><li><code>async</code> \u811A\u672C\u4E0D\u80FD\u4FDD\u8BC1\u987A\u5E8F, \u5B83\u662F\u72EC\u7ACB\u4E0B\u8F7D, \u72EC\u7ACB\u8FD0\u884C, \u4E0D\u4F1A\u7B49\u5F85\u5176\u4ED6\u811A\u672C</li><li><code>async</code> \u4E0D\u4F1A\u80FD\u4FDD\u8BC1\u5728 <code>DOMContentLoaded</code> \u4E4B\u524D\u6216\u8005\u4E4B\u540E\u6267\u884C</li></ul><p><code>defer</code> \u901A\u5E38\u7528\u4E8E\u9700\u8981\u5728\u6587\u6863\u89E3\u6790\u540E\u64CD\u4F5C <code>DOM</code> \u7684 <code>JavaScrip</code> t\u4EE3\u7801\uFF0C\u5E76\u4E14\u5BF9\u591A\u4E2A <code>script</code> \u6587\u4EF6\u6709\u987A\u5E8F\u8981\u6C42\u7684\uFF1B</p><p><code>async</code> \u901A\u5E38\u7528\u4E8E\u72EC\u7ACB\u7684\u811A\u672C\uFF0C\u5BF9\u5176\u4ED6\u811A\u672C\uFF0C\u751A\u81F3 <code>DOM</code> \u6CA1\u6709\u4F9D\u8D56\u7684</p>',59);function s(n,p){return t}var u=e(l,[["render",s],["__file","xuanranyuanli.html.vue"]]);export{u as default};
