        <span class="token keyword">return</span> <span class="token function">interval</span><span class="token punctuation">(</span><span class="token number">100</span> <span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token parameter">id<span class="token punctuation">,</span>resolve</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token punctuation">{</span>
