      <span class="token keyword">const</span> menu <span class="token operator">=</span> <span class="token punctuation">{</span><span class="token operator">...</span>route<span class="token punctuation">.</span>meta<span class="token operator">?.</span>menu<span class="token punctuation">,</span> routeName<span class="token operator">:</span> route<span class="token punctuation">.</span>name <span class="token keyword">as</span> <span class="token builtin">string</span><span class="token punctuation">}</span><span class="token punctuation">;</span>
      <span class="token keyword">const</span> isHas <span class="token operator">=</span> <span class="token keyword">this</span><span class="token punctuation">.</span>historyMenus<span class="token punctuation">.</span><span class="token function">some</span><span class="token punctuation">(</span>menu <span class="token operator">=></span> <span class="token punctuation">{</span>
