<span class="token keyword">const</span> <span class="token function-variable function">changeCity</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">city</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
