<span class="token keyword">const</span> <span class="token function-variable function">changeProvince</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token parameter">province</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
