<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span> <span class="token attr-name">setup</span> <span class="token attr-name">lang</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>ts<span class="token punctuation">"</span></span><span class="token punctuation">></span></span><span class="token script"><span class="token language-javascript">
<span class="token keyword">import</span> <span class="token punctuation">{</span> tableOptions <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">"../../components/table/src/types"</span><span class="token punctuation">;</span>
<span class="token keyword">const</span> tableData <span class="token operator">=</span> <span class="token punctuation">[</span>
<span class="token operator">...</span>
<span class="token punctuation">]</span><span class="token punctuation">;</span>
<span class="token keyword">const</span> <span class="token literal-property property">tableOptions</span><span class="token operator">:</span> tableOptions<span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token punctuation">[</span>
    <span class="token operator">...</span>
    <span class="token comment">// 最后一列</span>
    <span class="token punctuation">{</span>
