    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>footer</span> <span class="token attr-name">v-if</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>$route.path !== <span class="token punctuation">'</span>/welcome/4<span class="token punctuation">'</span><span class="token punctuation">"</span></span><span class="token punctuation">></span></span>
