    <span class="token keyword">return</span> <span class="token keyword">this</span><span class="token punctuation">.</span>instance<span class="token punctuation">.</span><span class="token generic-function"><span class="token function">request</span><span class="token generic class-name"><span class="token operator">&lt;</span><span class="token constant">R</span><span class="token operator">></span></span></span><span class="token punctuation">(</span><span class="token punctuation">{</span> <span class="token operator">...</span>config<span class="token punctuation">,</span> url<span class="token punctuation">,</span> data<span class="token punctuation">,</span> method<span class="token operator">:</span><span class="token string">'PATCH'</span> <span class="token punctuation">}</span><span class="token punctuation">)</span>
