<div class="language-javascript ext-js line-numbers-mode"><pre v-pre class="language-javascript"><code><span class="token operator">&lt;</span><span class="token operator">!</span><span class="token constant">DOCTYPE</span> html<span class="token operator">></span>
<span class="token operator">&lt;</span>html lang<span class="token operator">=</span><span class="token string">"en"</span><span class="token operator">></span>
<span class="token operator">&lt;</span>head<span class="token operator">></span>
  <span class="token operator">&lt;</span>meta charset<span class="token operator">=</span><span class="token string">"UTF-8"</span><span class="token operator">></span>
  <span class="token operator">&lt;</span>meta http<span class="token operator">-</span>equiv<span class="token operator">=</span><span class="token string">"X-UA-Compatible"</span> content<span class="token operator">=</span><span class="token string">"IE=edge"</span><span class="token operator">></span>
  <span class="token operator">&lt;</span>meta name<span class="token operator">=</span><span class="token string">"viewport"</span> content<span class="token operator">=</span><span class="token string">"width=device-width, initial-scale=1.0"</span><span class="token operator">></span>
  <span class="token operator">&lt;</span>title<span class="token operator">></span>Document<span class="token operator">&lt;</span><span class="token operator">/</span>title<span class="token operator">></span>
<span class="token operator">&lt;</span><span class="token operator">/</span>head<span class="token operator">></span>
<span class="token operator">&lt;</span>body<span class="token operator">></span>
  <span class="token operator">&lt;</span>svg width<span class="token operator">=</span><span class="token string">"800"</span> height<span class="token operator">=</span><span class="token string">"800"</span><span class="token operator">></span>
    <span class="token operator">&lt;</span>rect width<span class="token operator">=</span><span class="token string">"50"</span> height<span class="token operator">=</span><span class="token string">"50"</span> style<span class="token operator">=</span><span class="token string">"fill:red;stroke-width:2;stroke:green"</span><span class="token operator">></span><span class="token operator">&lt;</span><span class="token operator">/</span>rect<span class="token operator">></span>
    <span class="token operator">&lt;</span>line 
      x1<span class="token operator">=</span><span class="token string">"100"</span>
      y1<span class="token operator">=</span><span class="token string">"100"</span>
      x2<span class="token operator">=</span><span class="token string">"250"</span>
      y2<span class="token operator">=</span><span class="token string">"75"</span>
      style<span class="token operator">=</span><span class="token string">"stroke:blue;stroke-width:1"</span>
    <span class="token operator">></span><span class="token operator">&lt;</span><span class="token operator">/</span>line<span class="token operator">></span>
    <span class="token operator">&lt;</span>line 
      x1<span class="token operator">=</span><span class="token string">"250"</span>
      y1<span class="token operator">=</span><span class="token string">"75"</span>
      x2<span class="token operator">=</span><span class="token string">"300"</span>
      y2<span class="token operator">=</span><span class="token string">"50"</span>
      style<span class="token operator">=</span><span class="token string">"stroke:red;stroke-width:2"</span>
    <span class="token operator">></span><span class="token operator">&lt;</span><span class="token operator">/</span>line<span class="token operator">></span>
