  <span class="token keyword">if</span> <span class="token punctuation">(</span>c<span class="token punctuation">.</span>type <span class="token operator">!==</span> Tab<span class="token punctuation">)</span> <span class="token punctuation">{</span>              <span class="token comment">// 检查每个对象的 type !!</span>
    <span class="token keyword">throw</span> <span class="token keyword">new</span> <span class="token class-name">Error</span><span class="token punctuation">(</span><span class="token string">'Tabs的子标签必须是Tab'</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token keyword">const</span> titles <span class="token operator">=</span> defaults<span class="token punctuation">.</span><span class="token function">map</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">c</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token punctuation">{</span>    <span class="token comment">// 得到数组 ['导航1' , '导航2' , '导航3']</span>
