    <span class="token punctuation">}</span>
  <span class="token punctuation">}</span>
  <span class="token selector">&amp;.activated</span> <span class="token punctuation">{</span>
    <span class="token property">background</span><span class="token punctuation">:</span> #2d8df0<span class="token punctuation">;</span>
    <span class="token selector">span</span> <span class="token punctuation">{</span>
      <span class="token selector">left: calc(100% - #</span><span class="token punctuation">{</span>$h-circle<span class="token punctuation">}</span> - 2px<span class="token punctuation">)</span><span class="token punctuation">;</span> // 技巧 <span class="token function">calc</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
