      context<span class="token punctuation">.</span><span class="token function">emit</span><span class="token punctuation">(</span><span class="token string">'onChange'</span> <span class="token punctuation">,</span> modelValue<span class="token punctuation">.</span>value <span class="token operator">===</span> <span class="token boolean">true</span> <span class="token operator">?</span> <span class="token boolean">true</span> <span class="token operator">:</span><span class="token boolean">false</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>
    <span class="token keyword">return</span> <span class="token punctuation">{</span>toggle <span class="token punctuation">}</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">></span></span>
